import UIKit
import GoloSwift

// MARK: - Input & Output protocols
@objc protocol ActiveVotersShowRoutingLogic {
    func routeToVoterUserProfileShowScene(byUserNickName nickName: String)
}

protocol ActiveVotersShowDataPassing {
    var dataStore: ActiveVotersShowDataStore? { get }
}

class ActiveVotersShowRouter: NSObject, ActiveVotersShowRoutingLogic, ActiveVotersShowDataPassing {
    // MARK: - Properties
    weak var viewController: ActiveVotersShowViewController?
    var dataStore: ActiveVotersShowDataStore?
    
    
    // MARK: - Class Initialization
    deinit {
        Logger.log(message: "Success", event: .severe)
    }
    

    // MARK: - Routing
    func routeToVoterUserProfileShowScene(byUserNickName nickName: String) {
        let storyboard              =   UIStoryboard(name: "UserProfileShow", bundle: nil)
        let destinationVC           =   storyboard.instantiateViewController(withIdentifier: "UserProfileShowVC") as! UserProfileShowViewController
        destinationVC.sceneMode     =   .preview
        var destinationDS           =   destinationVC.router!.dataStore!
        
        passDataToUserProfileShowScene(userNickName: nickName, destination: &destinationDS)
        navigateToUserProfileShowScene(source: viewController!, destination: destinationVC)
    }

    
    // MARK: - Navigation
    func navigateToUserProfileShowScene(source: ActiveVotersShowViewController, destination: UserProfileShowViewController) {
        source.show(destination, sender: nil)
    }

    
    // MARK: - Passing data
    func passDataToUserProfileShowScene(userNickName: String, destination: inout UserProfileShowDataStore) {
        destination.userNickName    =   userNickName
    }
}
